mapper.update:
UPDATE post
SET

    readers = #{post.readers}


    comments = #{post.comments}


    status = #{post.status}


    title = #{post.title}


    , type = #{post.type}


    , content = #{post.content}


    , allow = #{post.allow}


    , post_reward = #{post.postReward}


    , reward_content = #{post.rewardContent}


    , reward_num = #{post.rewardNum}

WHERE id = #{post.id}

UPDATE post
SET

    wonderful = #{rank}


    top = #{rank}

WHERE id = #{id}

mapper.select:
SELECT p.*,u.id uid,u.nickName,u.avatar,u.auth,u.vip,c.id cid,c.name cname FROM post p,`user` u,`column` c,keyword k WHERE p.type = c.id AND p.author = u.id AND p.delete = 0 AND p.id = k.pid AND k.keyword = #{tag}
AND p.status = 0
AND p.status = 1
AND p.wonderful = 1
ORDER BY p.top DESC ,p.id DESC LIMIT #{page.startNumber},#{page.pageSize}
